mapper.select:
select

from user_info
where user_info.id <> 'admin'

	and user_info.name = #{name,jdbcType=VARCHAR}


	and user_info.id = #{id,jdbcType=VARCHAR}


	and user_info.org_id = #{orgId,jdbcType=VARCHAR}


	and user_info.email = #{email,jdbcType=VARCHAR}


	and user_info.tel = #{tel,jdbcType=VARCHAR}


	and user_info.creat_user = #{creatUser,jdbcType=VARCHAR}


	and user_info.modify_user = #{modifyUser,jdbcType=VARCHAR}
